import React, { Component } from "react";
import axios, { post } from "axios";

export default class NewRoutines extends Component {
  state = {
    testRes: "",
    options: { option: "", url: "" },
  };

  handleSubmit = () => {
    // event.preventDefault();

    const routine = "Regular Moazzam 1 Routine";

    axios
      .post(`http://127.0.0.1:8000/Testing/`, { routine })
      .then((res) => {
        this.setState({ testRes: res.data });
        console.log(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  //   -------------  Recorded Video Handler  ---------------------

  handleRecordedVideo = () => {
    const fileName = "/Video Name";

    axios
      .post(`http://127.0.0.1:8000/RecoredVideo/`, { FileUrl: fileName })
      .then((res) => {
        //   this.setState({ testRes: res.data })
        console.log(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  //    --------------------------------------

  onFileChange(e) {
    let files = e.target.files;
    let reader = new FileReader();
    reader.readAsDataURL(files[0]);

    reader.onload = (e) => {
      console.warn("video Data: ", e.target.result);
      const url = "http://127.0.0.1:8000/RecoredVideo/";
      const formData = { file: e.target.result };
      return post(url, formData).then((response) =>
        console.warn("post video request result", response)
      );
    };

    var formData = new FormData();
    var imagefile = document.querySelector("#file");
    formData.append("image", imagefile.files[0]);
    axios.post("upload_file", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  }

  render() {
    return (
      <div>
        <h1> Routines Registration Page:</h1>

        <div className="row">
          <div className="col">
            Routine Name: <input type="Routine Name" />
          </div>
        </div>

        <br>{/* Recorded Video Module */}</br>
        <div className="row">
          <div className="col">
            <input
              name="file"
              type="file"
              class="file"
              onChange={(e) => this.onFileChange(e)}
              accept=".mp4"
            />
            <button
              type="button"
              class="btn btn-primary"
              onClick={this.handleRecordedVideo}
            >
              {" "}
              Select Recorded Video
            </button>
          </div>
        </div>

        {/* Live Video Module */}

        <h3> Input Video Mode:</h3>

        <button class="btn btn-primary"> Using Kinect</button>

        <button class="btn btn-primary"> Using Embeded Camera</button>

        {/* Live Video Module End*/}

        <br />
        <br />

        <h3> Input Image Mode:</h3>

        <button className="btn btn-primary"> Select Image</button>
        <button className="btn btn-primary"> Capture Image </button>

        <br />
        <br />
        <div className="row">
          <div className="col-1"></div>
          <div className="col-4">IMage url {this.state.testRes}</div>
          <div className="col-1"></div>
          <div className="col-5 ">dvfgr</div>
          <div className="col-1"></div>
        </div>

        <br />

        <div className="row">
          <div className="col">
            <center>
              <button className="btn btn-warning"> Retry </button>
              <button className="btn btn-warning"> Back </button>
              <button className="btn btn-warning"> Next </button>
              <button className="btn btn-warning"> Save </button>
            </center>
          </div>
        </div>
      </div>
    );
  }
}
